definition.script:
pipeline {
    agent any
    stages {
        stage('CPIlint') {
            steps {
                echo 'CPIlint'
                sh '/cp/cpilint-1.0.4/bin/cpilint -rules /files/novo -files /files/novo'
            }
        }
        stage('CodeNarc') {
            steps {
                echo 'CodeNarc'
                sh 'mkdir /files/unzip_flow'
                sh 'unzip /files/firstflow.zip -d /files/novo'
                sh 'java -cp /cp/codenarc.jar org.codenarc.CodeNarc -rulesetfiles=file:/files/novo -basedir=/files/unzip_flow/novo'
                sh 'rm -rf /files/unzip_flow'
            }
        }
        stage('Dependency check') {
            steps {
                echo 'Dependency check'
                //sh '/cp/dependency-check/bin/dependency-check.sh -scan path -project name'
            }
        }
    }
}
