definition.script:
def FlowZip = '/files/novo.zip'
def CPILintRules = '/files/test.xml'
def CodenarcRules = '/files/rules.groovy'

pipeline {
    agent any
    options{
        skipStagesAfterUnstable()
    }
    stages {
        stage('Unzip') {
            steps {
                echo 'Unzip'
                sh 'mkdir /files/unzip_flow'
                sh "unzip ${FlowZip} -d /files/unzip_flow"
            }
        }
        stage('CPIlint') {
            steps {
                echo 'CPIlint'
                sh "/cp/cpilint-1.0.4/bin/cpilint -rules ${CPILintRules} -files ${FlowZip}"
            }
        }
        stage('CodeNarc') {
            steps {
                echo 'CodeNarc'
                sh "java -cp /cp/codenarc.jar org.codenarc.CodeNarc -report=console:stdout -rulesetfiles=file:${CodenarcRules} -basedir=/files/unzip_flow"
            }
        }
        stage('Dependency check') {
            steps {
                echo 'Dependency check'
                sh '/cp/dependency-check/bin/dependency-check.sh --nvdApiKey=f906660d-70f5-4201-ab55-5e04202f34f9 --format JSON --prettyPrint -scan /files/unzip_flow '
            }
        }
    }
    post {
        always {
            sh 'rm -rf /files/unzip_flow'
        }
    }
}
